import React, {useContext, useEffect, useState} from 'react'
import {NavLink, useHistory} from 'react-router-dom'
import {useHttp} from '../../hook/http.hook'
import { AuthContext } from '../../context/AuthContext'
import '../../index.css'
export const  AddTask = () =>{
  const history = useHistory()
  const auth = useContext(AuthContext)
  const {request} = useHttp()
  const [task, setTask] = useState({
      topic:'', body:''
  })
    
  const changeHandler = event => {
    setTask({ ...post, [event.target.name]: event.target.value })
  }
  const pressHandler = async() => {
         try {
        const data = await request('/api/taskfromusers/generate', 'POST', {...post}, {
          Authorization: `Bearer ${auth.token}`
        })
        history.push(`/theme/${data.task._id}`)
      } catch (e) {}
    }
    useEffect(() => {
        window.M.updateTextFields()
      }, [])

    return(
        <div className="create">
        <p className="pageName" style={{textAlign:"center", fontWeight:"900"}}>Создать задание</p>
            <div className="input-field  ">
                  <input 
                  placeholder="Веддите название задания" 
                  id="topic"
                  type="text"
                  name ="topic"
                  value={task.topic}
                  onChange={changeHandler}
                  />
                  <input 
                  placeholder="Веддите текст задания" 
                  id="body"
                  name = "body"
                  type="text"
                  value={task.body}
                  onChange={changeHandler}
                  />
            </div>
      
            <button 
                className="btn grey lighten-1 black-text"
                onClick={pressHandler}>
                Создать 
            </button>
        </div>
    )
    
}
