import React, { useContext, useState , useEffect} from 'react'
import {Link} from 'react-router-dom'

import {useHttp} from '../hook/http.hook'
import {AuthContext} from '../context/AuthContext'
import {Admin} from '../Admin/Admin'
export const AdminListOfComments = ({ comments }) => {

  console.log(comments)
  const [parsedComments, setParsedComments] = useState([])
  const {request} = useHttp()
  const auth = useContext(AuthContext)
  useEffect(()=> {
    if (comments){
      setParsedComments(comments)
    }
  }, [comments])
  if (!parsedComments.length) {
    return <p className="center"> Користувачів поки немає</p>
  }


  if (!comments.length) {
    return <p className="center">Кооментарів поки немає</p>
  }

  return (
      <div>
      <Admin/>
      <p>Коментарі</p>
    <table>
      <thead>
      <tr>
        <th>№</th>
        <th>Название</th>
        <th>Автор</th>
        <th>Дата создания</th>
        <th></th>
        <th></th>
      </tr>
      </thead>

      <tbody>
      {parsedComments && parsedComments.map((comment, index) => {
        return (
          <tr>
            <td>{index + 1}</td>
            <td>{comment.topic}</td>
            <td>{comment.author}</td>
            <td>{new Date(comment.date).toLocaleDateString()}</td>
           
            <th>Удалить</th>
            <th>Редактировать</th>
          </tr>
        )
      }) }
      </tbody>
    </table>
    </div>
  )
}
