import React, {useContext, useEffect, useState} from 'react'
import {useHttp} from '../hook/http.hook'
import {AuthContext} from '../context/AuthContext'
import {useHistory, useParams} from 'react-router-dom'

export const DoneCard = ({done}) => 
{
  console.log(done)
  const history = useHistory()
  const auth = useContext(AuthContext)
  const {request} = useHttp()
  const {id} = useParams()

  const pressHandler = async () => {
      try {
        const data = await request(`/api/decisive/delete/${id}`, 'DELETE', null, {
          Authorization: `Bearer ${auth.token}`
        })
        console.log(data)
       history.push(`/usertask/${done.task}`)
      } catch (e) {
          console.log()
      }
    }
  

    useEffect(() => 
    {
      window.M.updateTextFields()
    }, [])
  
  return (
      <div> 
      
          <div>
            <p  style={{paddingTop:'10px'}}>{done.decisiveuser}</p>

            <p  style={{paddingTop:'10px'}}>{done.decisiveuseremail}</p>
            <p  style={{paddingTop:'10px'}}>{done.checkinguser}</p>
            <p  style={{paddingTop:'10px'}}>{done.checkinguseremail}</p>
            <p  style={{paddingTop:'10px'}}>{done.text}</p>
            
            <p  style={{paddingTop:'10px'}}>{done.task}</p>
            <div style={{
                                    width:"20px", height:"20px", borderRadius:"50%", backgroundColor: done.checked?"green":"red"
                                }} />
            <div style={{
                                    width:"20px", height:"20px", borderRadius:"50%", backgroundColor: done.estimate?"green":"red"
                                }} />
            <p  style={{paddingTop:'10px'}}>{done.comment}</p>
          </div> 
          <button className="btn grey lighten-1 black-text"
                onClick={pressHandler}
                >Переробити
          </button>
          </div>
  )
}
