import React,{useContext, useState} from 'react'
import {useHttp} from '../../hook/http.hook'
import { AuthContext } from '../../context/AuthContext'

export const EstimateInfo = ({decisive}) => {
   const id = decisive._id
   const {request} = useHttp()
  const auth = useContext(AuthContext)
   const [decire, setDecire] = useState({
    comment:'', estimate:'', checked:''
})

const changeHandler = event =>{
    setDecire({...decire, [event.target.name]:event.target.value})
}

   const pressHandler = async() => {
          try {
            const data = await request(`/api/decisive/updatedicisive/${id}`, 'PUT', {
                decisiveuser:decisive._id,
                decisiveuseremail:decisive.decisiveuseremail,
                checkinguser: auth.userId,
                checkinguseremail: auth.userEmail, 
                text: decisive.text,
                task: decisive.task,
                ...decire
             }, {
            Authorization: `Bearer ${auth.token}`
         })
       } catch (e) {}
     }

     const [checked, setChecked] = useState(false);
    return (
     <>
         <h2>Решение</h2>
         
         <p>Айди задания{decisive.task}</p>
         <p>Текст решения:{decisive.text}</p>
         <p>{decisive.decisiveuseremail}</p>
         <form>
        <label>
          Проверено:
          <input
            name="isCheched"
            type="checkbox"
            defaultChecked={checked}
            onChange={() => setChecked(!checked)}
          />
        </label>
        <br />
        
      </form>
         <div>
         <div className="input-field">
          <input
            placeholder="Введіть комментар" 
                  id="comment"
                  name = "comment"
                  type="comment"
                  value={decire.comment}
                  onChange={changeHandler}
          />
        </div>
        <button 
                className="btn grey lighten-1 black-text"
                onClick={pressHandler}
                >Создать 
            </button>
         </div>
     </>    
    )
}
