import React from 'react'
import {Link} from 'react-router-dom'
import {Admin} from '../Admin/Admin'
export const AdminListOfUsers = ({ users }) => {
  if (!users.length) {
    return <p className="center"> Користувачів поки немає</p>
  }

  const pressHandler = async () => {
    try {
      const data = await request(`/api/decisioncreate/${id}`, 'POST', {body: comment}, {
        Authorization: `Bearer ${auth.token}`
      })
      console.log(data)
     // history.push(`/theme/${postId}`)
    } catch (e) {}
  }


  useEffect(() => 
  {
    window.M.updateTextFields()
  }, [])

  return (
      <div>
      <Admin/>
      <p>Користувачі</p>
    <table>
      <thead>
      <tr>
        <th>№</th>
        <th>Название</th>
        <th>Автор</th>
        <th>Рейтинг</th>
        <th></th>
        <th></th>
      </tr>
      </thead>

      <tbody>
      {users && users.map((user, index) => {
        return (
          <tr>
            <td>{index + 1}</td>
            <td>{user._id}</td>
            <td>{user.email}</td>
            <td>{user.rate}</td>
            <th><button onClick={()=>pressHandler(user._id)}>
                Видалити
            </button></th>
            <th>
            <button>Редагувати</button></th>
          </tr>
        )
      }) }
      </tbody>
    </table>
    </div>
  )
}
