import React,{useContext, useState} from 'react'
import {useHttp} from '../../hook/http.hook'
import { AuthContext } from '../context/AuthContext'

export const EstimateInfo = ({decisive}) => {
   const id = decisive._id
   
  const auth = useContext(AuthContext)
   const [decision, setDecision] = useState([])
     
   const pressHandler = async() => {
          try {
            console.log(decision)
            const data = await request(`/api/decisive/updatedicisive/${id}`, 'POST', {text: decision, decisiveuseremail: auth.userEmail}, {
            Authorization: `Bearer ${auth.token}`
         })
       } catch (e) {}
     }

    return (
     <>
         <h2>Решение</h2>
         
         <p>Айди задания{decisive.task}</p>
         <p>Текст решения:{decisive.text}</p>
         <p>{decisive.decisiveuseremail}</p>

         <div>
         <div className="input-field">
          <input
            placeholder="Введіть комментар" 
                  id="comment"
                  name = "comment"
                  type="comment"
                  value={decision.comment}
                  onChange={e => setDecision(e.target.value)}
          />
        </div>
        <button 
                className="btn grey lighten-1 black-text"
                onClick={pressHandler}
                >Создать 
            </button>
         </div>
     </>    
    )
}
