import React,{useContext, useEffect, useState} from 'react'
import {Link} from 'react-router-dom'

import {useHttp} from '../hook/http.hook'
import {AuthContext} from '../context/AuthContext'
import {Admin} from '../Admin/Admin'
export const AdminListOfThemes = ({ themes }) => {

  const [parsedThemes, setParsedThemes] = useState([])
  const {request} = useHttp()
  const auth = useContext(AuthContext)
  useEffect(()=> {
    if (themes){
      setParsedThemes(themes)
    }
  }, [themes])
  if (!parsedThemes.length) {
    return <p className="center">Тем для обсуджения пока нет</p>
  }


  const deleteHandler = async (id) => {
    try {
      const data = await request(`/api/forumtheme/user/${id}`, 'DELETE', null, {
        Authorization: `Bearer ${auth.token}`
      })
      
      setParsedThemes(data)
      
     // history.push(`/theme/${postId}`)
    } catch (e) {}
  }
  const putHandler = async (id, user) => {
    try {
      const data = await request(`/api/admin/changeuser/${id}`, 'PUT', user, {
        Authorization: `Bearer ${auth.token}`
      })
      
      setParsedThemes(data)
      
     // history.push(`/theme/${postId}`)
    } catch (e) {}
  }
 
  return (
      <div>
      <Admin/>
      <p>Теми</p>
    <table>
      <thead>
      <tr>
        <th>№</th>
        <th>Название</th>
        <th>Автор</th>
        <th>Дата создания</th>
        <th>Ссылка</th>
        <th></th>
        <th></th>
      </tr>
      </thead>

      <tbody>
      {themes.map((forumpost, index) => {
        return (
          <tr>
            <td>{index + 1}</td>
            <td>{forumpost.topic}</td>
            <td>{forumpost.authoremail}</td>
            <td>{new Date(forumpost.date).toLocaleDateString()}</td>
            <td>
              <Link to={`/detail/${forumpost._id}`}>Открыть</Link>
            </td>
            <th>Удалить</th>
            <th>Редактировать</th>
          </tr>
        )
      }) }
      </tbody>
    </table>
    </div>
  )
}
