import React, { useContext, useState , useEffect} from 'react'
import {AuthContext} from '../context/AuthContext'
import {useHttp} from '../hook/http.hook'
export const UserInfo = (user) => {
  const [parsedUser, setParsedUser] = useState(null)
  const {request} = useHttp()
  const auth = useContext(AuthContext)
  useEffect(()=> {
    if (user){
      setParsedUser(user)
    }
  }, [user])
  if (!parsedUser ) {
    return <p className="center"> Користувачів поки немає</p>
  }

  const pressHandler = async (id) => {
    try {
      const data = await request(`/api/forumtheme/user/${id}`, 'DELETE', null, {
        Authorization: `Bearer ${auth.token}`
      })
      
      setParsedUser(data)
      
     // history.push(`/theme/${postId}`)
    } catch (e) {}
  }

  return (
      <>
            <td><div>{user._id}</div></td>
            <td>{user.email}</td>
            <td>{user.rate}</td>
            <th><button onClick={()=>pressHandler(user._id)}>
                Видалити
            </button></th>
            <th>
            <button>Редагувати</button></th>
    </>
  )
}
