export const  ForumThemeCard = ({post}) => {
    return(
        <>

        <p>ForumThemeCard</p>
        <p style={{display:"flex", justifyContent:"space-between" }}><span></span><span></span></p>
        <p>Название темы: {post.topic}</p>
        <p>Текст темы:</p>
        <p>{post.body}</p>
        
        </>
    )
}
