import React, {useContext, useEffect, useState} from 'react'
import {useHttp} from '../hook/http.hook'
import {AuthContext} from '../context/AuthContext'
import {useHistory, useParams} from 'react-router-dom'

export const ForumThemeCard = ({post}) => {
  const history = useHistory()
  const auth = useContext(AuthContext)
  const {request} = useHttp()
  
  const postId = useParams().id
  const [comment, setComment] = useState({
    body:''
})
  
const changeHandler = event => {
    setComment({...comment, [event.target.name]: event.target.value })
}
  useEffect(() => {
    window.M.updateTextFields()
  }, [])

  const pressHandler = async () => {
   //TODO POST ID
      try {
        console.log('post')
        console.log(postId)
        const data = await request('/api/comment/create', 'POST', {...comment}, {
          Authorization: `Bearer ${auth.token}`
        })
        
        console.log(data)
      //  history.push(`/detail/${data.link._id}`)
      } catch (e) {}
    }
  

  return (
      <div>
        <p style={{display:"flex", justifyContent:"space-between" }}><span>{post.author}</span>{new Date(post.date).toLocaleDateString()}<span></span></p>
        <p>Название темы: {post.topic}</p>
        <p>Текст темы:</p>
        <p>{post.body}</p>
      <div className="row">
      <div className="col s8 offset-s2" style={{paddingTop: '2rem'}}>
        <div className="input-field">
          <input
            placeholder="Введите комментарий"
            id="body"
            type="text"
            name="body"
            value={comment.body}
            onChange={changeHandler}
          />
        </div>
        <button 
                className="btn grey lighten-1 black-text"
                onClick={pressHandler}>
                Создать 
            </button>
      </div>
    </div>
    </div>
  )
}
