import React, { useContext, useState , useEffect} from 'react'
import {Link} from 'react-router-dom'

import {useHttp} from '../hook/http.hook'
import {AuthContext} from '../context/AuthContext'
import {Admin} from '../Admin/Admin'
export const AdminListOfComments = ({ comments }) => {

  console.log(comments)
  const [parsedComments, setParsedComments] = useState([])
  const {request} = useHttp()
  const auth = useContext(AuthContext)
  useEffect(()=> {
    if (comments){
      setParsedComments(comments)
    }
  }, [comments])
  if (!parsedComments.length) {
    return <p className="center"> Користувачів поки немає</p>
  }


  if (!comments.length) {
    return <p className="center">Кооментарів поки немає</p>
  }

  return (
      <div>
      <Admin/>
      <p>Коментарі</p>
    <table>
      <thead>
      <tr>
        <th>№</th>
        <th>Название</th>
        <th>Автор</th>
        <th>Дата создания</th>
        <th></th>
        <th></th>
      </tr>
      </thead>

      <tbody>
      {parsedComments && parsedComments.map((comment) => {
        return (
          <tr key={comment._id}>
          <td><div>{comment._id}</div></td>
            <td><input value={comment.body} /></td>
            <td><input value={comment.date} /></td>
            <th><button className="btn grey lighten-1 black-text" >
                Видалити
            </button></th>
            <th>
            <button className="btn grey lighten-1 black-text" >Редагувати</button></th>
  
          </tr>
        )
      }) }
      </tbody>
    </table>
    </div>
  )
}
