import React,{useContext, useState} from 'react'
import {useHttp} from '../../hook/http.hook'
import { AuthContext } from '../../context/AuthContext'

export const EstimateInfo = ({decisive}) => {
   const id = decisive._id
   const {request} = useHttp()
  const auth = useContext(AuthContext)
  
  const [ischeckedestimate, setCheckedestimate] = useState(false);
  const [checked, setChecked] = useState(false);
 
   const [decire, setDecire] = useState({
    comment:'', estimate:ischeckedestimate, checked: checked
})

const changeHandler = event =>{
    setDecire({...decire, [event.target.name]:event.target.value})
}

   const pressHandler = async() => {
          try {
            const data = await request(`/api/decisive/updatedicisive/${id}`, 'PUT', {
                decisiveuser:decisive._id,
                decisiveuseremail:decisive.decisiveuseremail,
                checkinguser: auth.userId,
                checkinguseremail: auth.userEmail, 
                text: decisive.text,
                task: decisive.task,
                ...decire
             }, {
            Authorization: `Bearer ${auth.token}`
         })
       } catch (e) {}
     }

    return (
     <>
         <h2>Решение</h2>
         
         <p>Айди задания{decisive.task}</p>
         <p>Текст решения:{decisive.text}</p>
         <p>{decisive.decisiveuseremail}</p>
         <label >
         Оцінка позитивна:
         <input style={{    position: "inherit",   padding: "10px",    opacity: 1, pointerEvents: "auto"}}
            name="isPositive"
            type="checkbox"
            checked={ischeckedestimate}
            onChange={()=>setCheckedestimate(!ischeckedestimate)} />
            </label>
         <div>
         <label>
         Перевірено:
         <input style={{    position: "inherit",   padding: "10px",    opacity: 1,  pointerEvents: "auto"}}
            name="isCheck"
            type="checkbox"
            checked={checked}
            onChange={()=>setChecked(!checked)} />
            </label>
         <div></div>
         <div className="input-field">
          <input
            placeholder="Введіть комментар" 
                  id="comment"
                  name = "comment"
                  type="comment"
                  value={decire.comment}
                  onChange={changeHandler}
          />
        </div>
        <button 
                className="btn grey lighten-1 black-text"
                onClick={pressHandler}
                >Создать 
            </button>
         </div>
     </>    
    )
}
