import React, { useContext, useState , useEffect} from 'react'
import {Link} from 'react-router-dom'
import {Admin} from '../Admin/Admin'
import {AuthContext} from '../context/AuthContext'
import {useHttp} from '../hook/http.hook'
export const AdminListOfUsers = ({ users }) => {
  const [parsedUsers, setParsedUsers] = useState([])
  const {request} = useHttp()
  const auth = useContext(AuthContext)
  useEffect(()=> {
    if (users){
      setParsedUsers(users)
    }
  }, [users])
  if (!parsedUsers.length) {
    return <p className="center"> Користувачів поки немає</p>
  }

  const pressHandler = async (id) => {
    try {
      const data = await request(`/api/forumtheme/user/${id}`, 'DELETE', null, {
        Authorization: `Bearer ${auth.token}`
      })
      
      setParsedUsers(data)
      
     // history.push(`/theme/${postId}`)
    } catch (e) {}
  }
 

  return (
      <div>
      <Admin/>
      <p>Користувачі</p>
    <table>
      <thead>
      <tr>
        <th>№</th>
        <th>Название</th>
        <th>Автор</th>
        <th>Рейтинг</th>
        <th></th>
        <th></th>
      </tr>
      </thead>

      <tbody>
      {parsedUsers.length && parsedUsers.map((user, index) => {
        return (
          <tr key={user._id}>
            <td>{index + 1}</td>
            <td><div>{user._id}</div></td>
            <td>{user.email}</td>
            <td>{user.rate}</td>
            <th><button onClick={()=>pressHandler(user._id)}>
                Видалити
            </button></th>
            <th>
            <button>Редагувати</button></th>
          </tr>
        )
      }) }
      </tbody>
    </table>
    </div>
  )
}
