import React from 'react'
import {NavLink,Link} from 'react-router-dom'
export const DesicionList = ({decisions}) =>{
    if(!decisions.length){
        return <p className="center">Немає тем на форумі <NavLink to ="/forum/createtheme">Создать тему</NavLink></p>
    }
    return(
        <div>
        <div><NavLink to ="/forum/createtheme">Создать тему</NavLink></div>
            <table>
                <thead>
                    <tr>
                        <th>Теми</th>
                    </tr>
                </thead>
                <tbody>
                    {decisions.map(decision => {
                        return(
                            <tr>
                                <td>{decision.authoremail}</td>
                                <td>Дата:{new Date(decision.date).toLocaleTimeString()}</td>
                                <td>{decision.topic}</td>
                                <td>
                                <Link to={`/theme/${decision._id}`}>Открыть</Link>
                                </td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        </div>
    )
}
