import React, {useContext, useEffect, useState} from 'react'
import {NavLink, useHistory} from 'react-router-dom'
import {useHttp} from '../../hook/http.hook'
import { AuthContext } from '../../context/AuthContext'
import '../../index.css'
export const  CreateTheme = () =>{
    const history = useHistory()
  const auth = useContext(AuthContext)
  const {request} = useHttp()
  const [post, setPost] = useState({
      topic:'', body:''
  })
    
  const changeHandler = event => {
    setPost({ ...post, [event.target.name]: event.target.value })
  }
  const pressHandler = async() => {
         try {
        const data = await request('/api/forumtheme/create', 'POST', {...post}, {
          Authorization: `Bearer ${auth.token}`
        })
        history.push(`/theme/${data.post._id}`)
      } catch (e) {}
    }
  

    return(
        <div className="create">
        <p className="pageName" style={{textAlign:"center", fontWeight:"900"}}>Создать тему</p>
            <div className="input-field  ">
                  <input 
                  placeholder="Веддите название темы" 
                  id="topic"
                  type="text"
                  value={post.topic}
                  onChange={changeHandler}
                  />
                  <input 
                  placeholder="Веддите текст темы" 
                  placeholder="Веддите название темы" 
                  id="body"
                  type="text"
                  value={post.body}
                  onChange={changeHandler}
                  />
            </div>
      
            <button 
                className="btn grey lighten-1 black-text"
                >
                Создать 
            </button>
        </div>
    )
    
}
