import React, { useContext, useState , useEffect} from 'react'
import {AuthContext} from '../context/AuthContext'
import {useHttp} from '../hook/http.hook'
export const UserInfo = ({user, deleteHandler}) => {
  const [parsedUser, setParsedUser] = useState(null)
  const {request} = useHttp()
  const auth = useContext(AuthContext)
  useEffect(()=> {
    if (user){
      setParsedUser(user)
    }
  }, [user])
  if (!parsedUser ) {
    return <p className="center"> Користувачів поки немає</p>
  }
  console.log(user)
 

  return (
      <>
            <td><div>{user._id}</div></td>
            <td><input>{user.email}</input></td>
            <td>{user.rate}</td>
            <th><button onClick={()=>deleteHandler(user._id)}>
                Видалити
            </button></th>
            <th>
            <button>Редагувати</button></th>
    </>
  )
}
