import React from 'react'
import {NavLink} from 'react-router-dom'
export const ForumList = ({themes}) =>{
    if(!themes.length){
        return 
        <div> <div><NavLink to ="/forum/createtheme">Создать тему</NavLink></div>
        <p className="center">Немає тем на форумі </p>
        </div>
    }
    return(
        <div>
        <div><NavLink to ="/forum/createtheme">Создать тему</NavLink></div>
            <table>
                <thead>
                    <tr>
                        <th>Комментарии</th>
                    </tr>
                </thead>
                <tbody>
                    {themes.map(thema => {
                        return(
                            <tr>
                                <td>Автор:{thema.author}</td>
                                <td>Дата:{new Date(thema.date).toLocaleTimeString()}</td>
                                <td>Текст:{thema.topic}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        </div>
    )
}
