import React from 'react'
import {NavLink,Link} from 'react-router-dom'
export const ForumList = ({themes}) =>{
    if(!themes.length){
        return <p className="center">Немає тем на форумі <NavLink to ="/forum/createtheme">Створити тему</NavLink></p>
    }
    return(
        <div>
        <div><NavLink to ="/forum/createtheme">Створити тему</NavLink></div>
            <table>
                <thead>
                    <tr>
                        <th>Теми</th>
                    </tr>
                </thead>
                <tbody>
                    {themes.map(thema => {
                        return(
                            <tr>
                            <td>{thema.topic}</td>
                                <td>{thema.authoremail}</td>
                                <td>Дата:{new Date(thema.date).toLocaleTimeString()}</td>
                                
                                <td>
                                <Link to={`/theme/${thema._id}`}>Открыть</Link>
                                </td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        </div>
    )
}
