import React, {useContext, useEffect, useState} from 'react'
import {useHttp} from '../hook/http.hook'
import {AuthContext} from '../context/AuthContext'
import {useHistory, useParams} from 'react-router-dom'

export const ForumThemeCard = ({post, comments}) => 
{
  console.log(comments)
  const history = useHistory()
  const auth = useContext(AuthContext)
  const {request} = useHttp()
  const [comment, setComment] = useState('')
  const {id} = useParams()

  const pressHandler = async () => {
      try {
        const data = await request(`/api/comment/${id}`, 'POST', {body: comment}, {
          Authorization: `Bearer ${auth.token}`
        })
        console.log(data)
       // history.push(`/theme/${postId}`)
      } catch (e) {}
    }
  

    useEffect(() => 
    {
      window.M.updateTextFields()
    }, [])
  
  return (
    <div>
      <div>
        <p style={{display:"flex", justifyContent:"space-between" }}><span>{post.author}</span>{new Date(post.date).toLocaleDateString()}<span></span></p>
        <p>Название темы: {post.topic}</p>
        <p>Текст темы:</p>
        <p>{post.body}</p>
      <div className="row">
      <div className="col s8 offset-s2" style={{paddingTop: '2rem', border:'solid lightgray 1px', marginBottom:'15px',
  boxShadow: '0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22)' }}>
        <div className="input-field" >
          <input
            placeholder="Введите комментарий"
            id="comment"
            type="text"
            value={comment}
            onChange={e => setComment(e.target.value)}
          />
        </div> 
        <button style={{marginBottom:'25px'}}
                className="btn grey lighten-1 black-text"
                onClick={pressHandler}>
                Создать 
            </button>
      </div>
      <div > 
      {comments && comments.map(comment => {
        console.log(comment)
        return(
          <div>
          <div >
            <strong>{comment.author}</strong>
            <p style={{paddingTop:'1px'}}>{new Date(comment.date).toLocaleTimeString()}</p>
            <p>{comment.body}</p>
          </div> 
          </div>
        )
      })}
      </div>
                   
        
    </div>
    </div>
    </div>
  )
}
