import React,{useContext,useCallback,useEffect,useState} from 'react'
import {NavLink, useHistory} from 'react-router-dom'
import { AuthContext } from '../context/AuthContext'
import {useHttp} from '../hook/http.hook'

import "../index.css"
const {auth} = require('..')
export const Navbar = () => {
    const history = useHistory()
   const auth = useContext(AuthContext)
   const [users, setUsers] = useState([])
   const {loading, request} = useHttp()
   const {token} = useContext(AuthContext)
 
   const fetchUsers = useCallback(async () => {
     try {
       const fetched = await request('/api/rate/${user.userId}', 'GET', null, {
         Authorization: `Bearer ${token}`
       })
       setUsers(fetched)
     } catch (e) {}
   }, [token, request])
 
   useEffect(() => {
     fetchUsers()
   }, [fetchUsers])
 
 const logoutHandler= event =>{
    event.preventDefault()
    auth.logout()
    history.push('/')
 }
 

    //TODO Удалить создать задание и реализовать переход по ссылке 
    return (
        <nav>
            <div className="nav-wrapper  blue lighten-1" style = {{padding:'0 2rem'}}>
                
                <ul clasName="nav-mobile">
                <li className="logo">Diplom</li>
                    <li><NavLink to ="/theory">Теорія</NavLink></li>
                    <li><NavLink to ="/practice">Практика</NavLink></li>
                    <li><NavLink to ="/forum">Форум</NavLink></li>
                    <li><NavLink to ="/userrate">Рейтинг</NavLink></li>
                    <li><NavLink to ="/admin">СуперЮзер</NavLink></li>
                    <li className="fromUsers" style={{color: '#ff0000',  fontWeight:900}}><NavLink to ="/superuser/addtask"> Создать Задание</NavLink></li>
                    <li><a href="/" onClick={logoutHandler}>Выйти</a></li>
                </ul>
            </div>
        </nav>
    )
}
