    return (
     <>
         <h2>Задание</h2>
         <p>Название задания:{taskfromusers.topic}</p>
         <p>Суть задания:{taskfromusers.body}</p>
         <p>Дата создания:{new Date(taskfromusers.date).toLocaleDateString()}</p>
         <p>Создал:{taskfromusers.author}</p>
         <div>
         <div className="input-field">
          <input
            placeholder="Введите ответ"
            id="body"
            type="text"
            name="body"
          />
        </div>
        <button 
                className="btn grey lighten-1 black-text"
                 
                >
                
                Создать 
            </button>
         </div>
     </>    
    )
}
