import React, {useContext, useEffect, useState} from 'react'
import {useHttp} from '../../hook/http.hook'
import {AuthContext} from '../../context/AuthContext'
import {useHistory} from 'react-router-dom'

export const  AddTask = () =>{
    const history = useHistory()
    const auth = useContext(AuthContext)
    const {request} = useHttp()
    useEffect(() => {
        window.M.updateTextFields()
      })

    const [taskfromusers, setTask]= useState({
        topic:'', body:''
    })
    const changeHandler = event =>{
        setTask({...taskfromusers, [event.target.name]:event.target.value})
    }
    const addHandler = async event =>{
        try{
            const data = await request('/api/taskfromusers/generate', 'POST', {...taskfromusers},
            {
                Authorization: `Bearer ${auth.token}`
            })
            console.log(data)
            history.push('/taskfromusers/${data.taskfromusers._id')
        }
        catch(e){

        }
    }
    return(
        <div className="createTask">
        <p className="pageName" style={{textAlign:"center", fontWeight:"900"}}>Create Task</p>
        <div className="col s8 offset-s2" style={{paddingTop:'2rem'}}> 
            <div className="input-field">
                <input
                    placeholder="Введите название задания"
                    id="topic"
                    type="text"
                    value = {taskfromusers.topic}
                    className="yellow-input"
                    name="topic"
                    onChange={changeHandler}
                    />
                    
                  <label htmlFor="topic">Название</label>
                     <input
                    placeholder="Введите текст задания"
                    id="body"
                    type="text"
                    value = {taskfromusers.body}
                    className="yellow-input"
                    onChange={changeHandler}
                    name="body"
                    />
                    
                  <label htmlFor="body">Задание</label>
            </div>
            <div className="card-action">
                    <button 
                    className="btn yellow darken-4" 
                    style={{marginRight:10}}
                    onClick={addHandler}>
                    Создать
                    </button>
            </div>
        </div>
        </div>
    )
}
